import React from 'react';
import { useLocation, useParams } from 'react-router-dom';

interface CourseInfo {
    name: string;
    par: number[];
}

const Hole: React.FC = () => {
    const { state } = useLocation();
    const { holeNumber } = useParams<{ holeNumber?: string }>(); // Allow `holeNumber` to be optional
    const courseInfo = state as CourseInfo;

    const holeIndex = holeNumber ? parseInt(holeNumber) - 1 : 0; // Default to hole 1 if undefined
    const parForHole = courseInfo.par[holeIndex]; // Get the par for the current hole

    return (
        <div className="hole-container">
            <h1>{courseInfo.name}</h1>
            <h2>Hole {holeNumber || 1}</h2>
            <p>Par: {parForHole}</p>
        </div>
    );
};

export default Hole;
