import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/Home';
import Game from './components/Game';
import Leaderboard from './components/Leaderboard';
import Hole from './components/Hole'; // Import the Hole component
import './App.css';

interface CourseInfo {
    name: string;
    par: number[];
}

const App: React.FC = () => {
    const [currentPage, setCurrentPage] = useState<'home' | 'game' | 'leaderboard'>('home');
    const [players, setPlayers] = useState<string[]>([]);
    const [strokes, setStrokes] = useState<string[][][]>([]);
    const [shotTypes, setShotTypes] = useState<string[][][]>([]);
    const [currentHole, setCurrentHole] = useState(1);
    const [selectedCourse, setSelectedCourse] = useState<CourseInfo | null>(null);

    const startGame = (playerNames: string[], courseInfo: CourseInfo | null) => {
        setPlayers(playerNames);
        setSelectedCourse(courseInfo);

        const initialStrokes = Array.from({ length: 18 }, () =>
            Array.from({ length: playerNames.length }, () => [])
        );
        const initialShotTypes = Array.from({ length: 18 }, () =>
            Array.from({ length: playerNames.length }, () => [])
        );

        setStrokes(initialStrokes);
        setShotTypes(initialShotTypes);
        setCurrentHole(1);
        setCurrentPage('game');
    };

    return (
        <Router>
            <div className="App">
                <Routes>
                    <Route path="/" element={<Home startGame={startGame} />} />
                    <Route
                        path="/hole/:holeNumber"
                        element={
                            selectedCourse && (
                                <Hole holeNumber={currentHole} courseInfo={selectedCourse} />
                            )
                        }
                    />
                    <Route
                        path="/leaderboard"
                        element={
                            <Leaderboard
                                players={players}
                                strokes={strokes}
                                shotTypes={shotTypes}
                                goToGame={() => setCurrentPage('game')}
                            />
                        }
                    />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
