import React from 'react';

interface HoleProps {
    holeNumber: number; // Current hole number (1-based index)
    courseInfo: {
        name: string;
        par: number[];
        yardage: number[];
    };
    totalStrokes?: number; // Optional, if you want to pass total strokes
}

const Hole: React.FC<HoleProps> = ({ holeNumber, courseInfo, totalStrokes }) => {
    const parForHole = courseInfo.par[holeNumber - 1]; // Get the par for the current hole

    return (
        <div className="hole-container">
            <h1 style={{ textAlign: 'center', color: '#2380d7', marginBottom: 0 }}>{courseInfo.name}</h1>
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', marginTop: 24, marginBottom: 24 }}>
                <div
                    style={{
                        background: '#e3f2fd',
                        borderRadius: '12px',
                        padding: '18px 40px',
                        color: '#1976d2',
                        fontWeight: 'bold',
                        fontSize: '2rem',
                        boxShadow: '0 2px 8px rgba(25, 118, 210, 0.08)',
                        marginRight: 32,
                        minWidth: 220,
                        textAlign: 'center'
                    }}
                >
                    Hole {holeNumber} &nbsp;|&nbsp; Par {parForHole}
                </div>
                {typeof totalStrokes === 'number' && (
                    <div
                        style={{
                            marginLeft: 'auto',
                            background: '#fffde7',
                            borderRadius: '12px',
                            padding: '18px 32px',
                            color: '#f9a825',
                            fontWeight: 'bold',
                            fontSize: '1.5rem',
                            boxShadow: '0 2px 8px rgba(249, 168, 37, 0.08)',
                            minWidth: 180,
                            textAlign: 'center'
                        }}
                    >
                        Total Strokes: {totalStrokes}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Hole;
