import React from 'react';

interface LeaderboardProps {
    players: string[];
    strokes: string[][];
    shotTypes: string[][];
    goToGame: () => void;
}

const Leaderboard: React.FC<LeaderboardProps> = ({ players, strokes, shotTypes, goToGame }) => {
    return (
        <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
            <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>Leaderboard</h1>
            <div style={{ overflowX: 'auto', marginBottom: '20px' }}>
                <table
                    style={{
                        width: '100%',
                        borderCollapse: 'collapse',
                        backgroundColor: '#ffffff',
                        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                    }}
                >
                    <thead>
                        <tr style={{ backgroundColor: '#1976d2', color: 'white' }}>
                            <th style={{ padding: '10px', textAlign: 'left', whiteSpace: 'nowrap' }}>Player</th>
                            <th style={{ padding: '10px', textAlign: 'left', whiteSpace: 'nowrap' }}>Total Strokes</th>
                            <th style={{ padding: '10px', textAlign: 'left', whiteSpace: 'nowrap' }}>Shot Types</th>
                        </tr>
                    </thead>
                    <tbody>
                        {players.map((player, index) => (
                            <tr key={index}>
                                <td style={{ padding: '10px', border: '1px solid #ccc' }}>{player}</td>
                                <td style={{ padding: '10px', border: '1px solid #ccc' }}>
                                    {strokes.reduce((total, hole) => total + (hole[index] ? 1 : 0), 0)}
                                </td>
                                <td style={{ padding: '10px', border: '1px solid #ccc' }}>
                                    {shotTypes
                                        .map((hole) => hole[index])
                                        .filter(Boolean)
                                        .join(', ')}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div style={{ textAlign: 'center' }}>
                <button
                    onClick={goToGame}
                    style={{
                        padding: '10px 20px',
                        backgroundColor: '#1976d2',
                        color: 'white',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                    }}
                >
                    Back to Game
                </button>
            </div>
        </div>
    );
};

export default Leaderboard;
