import React from 'react';

interface HoleProps {
    holeNumber: number; // Current hole number (1-based index)
    courseInfo: {
        name: string;
        par: number[];
        yardage: number[];
    };
}

const Hole: React.FC<HoleProps> = ({ holeNumber, courseInfo }) => {
    const parForHole = courseInfo.par[holeNumber - 1]; // Get the par for the current hole

    return (
        <div className="hole-container">
            <h1>{courseInfo.name}</h1>
            <div
                style={{
                    margin: '20px auto',
                    padding: '12px 32px',
                    background: '#e3f2fd',
                    borderRadius: '10px',
                    display: 'inline-block',
                    fontSize: '1.5rem',
                    color: '#1976d2',
                    fontWeight: 'bold',
                    boxShadow: '0 2px 8px rgba(25, 118, 210, 0.08)'
                }}
            >
                Par for this hole: <span style={{ color: '#1565c0' }}>{parForHole}</span>
            </div>
        </div>
    );
};

export default Hole;
